CREATE OR REPLACE FUNCTION obter_se_tipo_atend_versao ( cd_tipo_atend_p text, cd_convenio_p bigint, cd_estabelecimento_p bigint, ds_versao_tiss_inicial_p text, ds_versao_tiss_final_p text, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
ie_apresenta_w		varchar(1) := 'S';
ds_versao_conv_w	bigint;

BEGIN

cd_estabelecimento_w := coalesce(cd_estabelecimento_p,wheb_usuario_pck.get_cd_estabelecimento);

if ((cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') and ((ds_versao_tiss_inicial_p IS NOT NULL AND ds_versao_tiss_inicial_p::text <> '') or (ds_versao_tiss_final_p IS NOT NULL AND ds_versao_tiss_final_p::text <> '')))then

	ds_versao_conv_w := Somente_numero(TISS_OBTER_VERSAO(cd_convenio_p, cd_estabelecimento_w, dt_referencia_p));	

	if (ds_versao_conv_w not between somente_numero(ds_versao_tiss_inicial_p) and somente_numero(ds_versao_tiss_final_p)) then
		ie_apresenta_w := 'N';
	end if;

else
	ie_apresenta_w := 'S';
end if;

return ie_apresenta_w;

end;
