CREATE OR REPLACE FUNCTION obter_se_tipo_aten_agenda ( ie_tipo_atendimento_p bigint, cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

ie_possui_tipo_w	varchar(1);
ie_consiste_w	varchar(1):= 'N';

BEGIN

select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_possui_tipo_w
from   	agenda_tipo_atendimento
where  	cd_agenda = cd_agenda_p;

if (ie_possui_tipo_w = 'S') then
	select 	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_consiste_w
	from   	agenda_tipo_atendimento
	where  	cd_agenda 		= cd_agenda_p
	and	ie_tipo_atendimento    	= ie_tipo_atendimento_p;
end if;

return ie_consiste_w;

end;
