SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_tipo_ativ_lib_usu_man ( nr_seq_tipo_ativ_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(01) := 'N';
qt_existe_w		bigint;
cd_perfil_w		perfil.cd_perfil%type;


BEGIN
cd_perfil_w				:= wheb_usuario_pck.get_cd_perfil;

if (coalesce(nr_seq_tipo_ativ_p,0) > 0) then
	select	count(*)
	into STRICT	qt_existe_w
	from	usuario_tipo_ativ_lib_man
	where	nr_seq_tipo_ativ = nr_seq_tipo_ativ_p;

	if (qt_existe_w > 0) then
		/*Verifica a existencia de regras gerais, se existir, está liberado*/

		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	usuario_tipo_ativ_lib_man
		where	nr_seq_tipo_ativ 	= nr_seq_tipo_ativ_p
		and	coalesce(nm_usuario_lib::text, '') = ''
		and	coalesce(nr_seq_grupo_trabalho::text, '') = ''
		and	coalesce(cd_perfil::text, '') = ''  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'N';
		end;
		/*Verifica se o usuário possui regra por usuário ou por grupo de trabalho*/

		if (ds_retorno_w = 'N') then
			begin
			select	ds_retorno
			into STRICT	ds_retorno_w
			from (	SELECT	'S' ds_retorno
				from	usuario_tipo_ativ_lib_man
				where	nr_seq_tipo_ativ		= nr_seq_tipo_ativ_p
				and	nm_usuario_lib		= nm_usuario_p
				
union

				SELECT	'S' ds_retorno
				from	usuario_tipo_ativ_lib_man
				where	nr_seq_tipo_ativ	= nr_seq_tipo_ativ_p
				and	cd_perfil		= cd_perfil_w 
				
union

				select	'S'
				from	man_grupo_trab_usuario c,
					man_grupo_trabalho b,
					usuario_tipo_ativ_lib_man a
				where	a.nr_seq_tipo_ativ		= nr_seq_tipo_ativ_p
				and	a.nr_seq_grupo_trabalho 	= b.nr_sequencia
				and	c.nr_seq_grupo_trab	= b.nr_sequencia
				and	a.nr_seq_grupo_trabalho 	= c.nr_seq_grupo_trab
				and	c.nm_usuario_param	= nm_usuario_p  LIMIT 1) alias1;
			exception
			when others then
				ds_retorno_w := 'N';
			end;
		end if;
	/*Se não existir nenhuma regra está liberado para todos (padrão)*/

	elsif (qt_existe_w = 0) then
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
