CREATE OR REPLACE FUNCTION obter_se_tipo_bloqueado (nr_atendimento_p bigint, cd_convenio_p bigint, ie_tipo_bloqueio_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1):= 'N';
nr_seq_motivo_w		bigint;
ie_bloqueio_w		varchar(1);
qt_tipo_bloqueio_w	bigint;
ie_tipo_convenio_w	smallint;

C01 CURSOR FOR
	SELECT	nr_seq_motivo
	from	bloqueio_titulo_paciente
	where	nr_atendimento = nr_atendimento_p
	order by nr_sequencia;


BEGIN

select	max(ie_tipo_convenio)
into STRICT	ie_tipo_convenio_w
from	convenio
where	cd_convenio = cd_convenio_p;

open C01;
loop
fetch C01 into
	nr_seq_motivo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	nr_seq_motivo_w:= nr_seq_motivo_w;

	end;
end loop;
close C01;

select 	count(*)
into STRICT	qt_tipo_bloqueio_w
from 	tipo_bloqueio_atend
where 	ie_tipo_bloqueio = ie_tipo_bloqueio_p
and 	nr_seq_motivo = nr_seq_motivo_w
and	coalesce(ie_tipo_convenio, coalesce(ie_tipo_convenio_w,0)) = coalesce(ie_tipo_convenio_w,0)
and 	ie_situacao = 'A';

if (qt_tipo_bloqueio_w = 0) then
	ie_retorno_w:= 'N';
else
	ie_retorno_w:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
