SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_tipo_consulta_pepa ( nr_seq_agenda_cons_p bigint, nr_seq_tipo_cons_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1):= 'N';
cd_especialidade_w	  bigint;
tem_especialidades_w	bigint;
			

BEGIN

    begin
        select 	
              coalesce(a.cd_especialidade, b.cd_especialidade) cd_especialidade
        into STRICT	
              cd_especialidade_w
        from	
              agenda_consulta a, 
              agenda b
        where	a.nr_sequencia = nr_seq_agenda_cons_p 
        and     a.cd_agenda = b.cd_agenda;
    exception when others then
        cd_especialidade_w := null;
    end;

    --nao tem especialidade definida no agendamento nem na agenda.
    if (coalesce(cd_especialidade_w::text, '') = '') then
        ie_retorno_w := 'S';
    else
        select 
              count(1)
        into STRICT 
              tem_especialidades_w
        from 
            ehr_tipo_registro a, 
            ehr_item_adicional b
        where b.nr_seq_tipo_reg = a.nr_sequencia
        and a.nr_sequencia = nr_seq_tipo_cons_p;

        --tem especialidade definida no agendamento/consulta, mas tipo de consulta nao possui especialidade definida, entao mostrar
        if (tem_especialidades_w = 0) then
            ie_retorno_w := 'S';
        else
            --tem especialidade definida no agendamento/consulta, verificacao se especialidade do tipo de consulta eh o mesmo do agendamento/consulta
            select
                count(1)
            into STRICT 
                tem_especialidades_w
            from 
                ehr_tipo_registro a, 
                ehr_item_adicional b
            where b.nr_seq_tipo_reg = a.nr_sequencia
            and a.nr_sequencia = nr_seq_tipo_cons_p
            and b.cd_especialidade = cd_especialidade_w;

            if (tem_especialidades_w = 0) then
                ie_retorno_w := 'N';
            else
                ie_retorno_w := 'S';
            end if;
        end if;

    end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
