CREATE OR REPLACE FUNCTION obter_se_tipo_documentacao_lib ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';
qt_existe_w		bigint;
cd_perfil_w		integer := obter_perfil_ativo;

C01 CURSOR FOR
	SELECT	coalesce(ie_visualiza,'S')
	from	tipo_documentacao_lib
	where	nr_seq_tipo_doc		= nr_sequencia_p
	and	coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w
	order by coalesce(cd_perfil,0), nr_sequencia;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	tipo_documentacao_lib
where	nr_seq_tipo_doc = nr_sequencia_p  LIMIT 1;

if (qt_existe_w > 0) then
	begin
	select ie_visualiza
	into STRICT ie_retorno_w
	from (	SELECT	coalesce(ie_visualiza,'S') ie_visualiza
		from	tipo_documentacao_lib
		where	nr_seq_tipo_doc		= nr_sequencia_p
		and	coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w
		order by coalesce(cd_perfil,0) desc, nr_sequencia desc ) alias4 LIMIT 1;
	exception
	when others then
		ie_retorno_w := 'N';
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
