CREATE OR REPLACE FUNCTION obter_se_tipo_doc_conselho ( nr_seq_tipo_doc_p bigint, nr_seq_conselho_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(01) := 'S';
qt_existe_w	bigint;


BEGIN

if (nr_seq_tipo_doc_p > 0) and (nr_seq_conselho_p > 0) then
	select	count(*)
	into STRICT	qt_existe_w
	from	tipo_documentacao_conselho
	where	nr_seq_tipo_doc = nr_seq_tipo_doc_p;

	if (qt_existe_w = 0) then
		ds_retorno_w := 'S';
	else
		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	tipo_documentacao_conselho
		where	nr_seq_tipo_doc = nr_seq_tipo_doc_p
		and	nr_seq_conselho = nr_seq_conselho_p
		and	clock_timestamp() between coalesce(dt_inicio_vigencia,clock_timestamp()) and coalesce(dt_fim_vigencia,clock_timestamp())  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'N';
		end;
	end if;
end if;

return	ds_retorno_w;

end;
