CREATE OR REPLACE FUNCTION obter_se_tipo_doc_lib_fornec ( cd_estabelecimento_p bigint, cd_cgc_p text, nr_seq_tipo_doc_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


qt_existe_w smallint;
ie_restringe_documento_w varchar(1);
ds_returno_w varchar(1);


BEGIN

select	coalesce(max(obter_valor_param_usuario(6, 35, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p)),'N')
into STRICT	ie_restringe_documento_w
;

if (ie_restringe_documento_w = 'S') then

	SELECT COUNT(*)
	into STRICT qt_existe_w
	FROM TIPO_PESSOA_DOCUMENTO
	WHERE nr_seq_tipo_doc_fornec = nr_seq_tipo_doc_p
	AND cd_tipo_pessoa IN (	SELECT cd_tipo_pessoa
	  			FROM pessoa_juridica
				WHERE cd_cgc = cd_cgc_p
				
UNION ALL

				SELECT cd_tipo_pessoa
				FROM PESSOA_JUR_TIPO_ADIC
				WHERE cd_cgc = cd_cgc_p
				AND cd_estabelecimento = cd_estabelecimento_p);

	if (qt_existe_w > 0) then
		ds_returno_w := 'S';
	else
		ds_returno_w := 'N';
	end if;
else
	ds_returno_w := 'S';
end if;

return ds_returno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
