SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_tipo_ganho_perda_lib ( nr_seq_tipo_pg_p bigint, nr_atendimento_p bigint default null) RETURNS varchar AS $body$
DECLARE


qt_itens_w	bigint;
ds_retorno_w	varchar(10);
ie_permite_w	varchar(1);
cd_setor_atendimento_w	bigint;
cd_estabelecimento_w	bigint;
cd_perfil_w				bigint   := Obter_perfil_ativo;


C01 CURSOR FOR
	SELECT 	l.ie_permite
	from 	TIPO_PERDA_GANHO t,
		grupo_perda_ganho g,
		grupo_perda_ganho_lib l
	where 	t.nr_sequencia = nr_seq_tipo_pg_p
	and   	t.nr_seq_grupo = g.nr_sequencia
	and   	g.nr_sequencia = l.nr_seq_grupo
	and   	coalesce(l.cd_setor_atendimento,cd_setor_atendimento_w) = cd_setor_atendimento_w
	and		coalesce(l.cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w
	and		coalesce(l.cd_perfil,cd_perfil_w) = cd_perfil_w
	order	by coalesce(l.cd_setor_atendimento,0),
				coalesce(l.cd_estabelecimento,0);
				
				
				
C02 CURSOR FOR
	SELECT	coalesce(ie_permite,'S')
	from	tipo_perda_ganho_regra
	where	nr_seq_tipo_pg	= nr_seq_tipo_pg_p
	and	coalesce(cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w
	 order by coalesce(cd_estabelecimento,0);



BEGIN

select	count(*)
into STRICT	qt_itens_w
from	tipo_perda_ganho_regra
where	nr_seq_tipo_pg	= nr_seq_tipo_pg_p;

cd_estabelecimento_w	:= coalesce(wheb_usuario_pck.get_cd_estabelecimento,1);

if (qt_itens_w = 0) then
	ds_retorno_w  :='S';
else
	ds_retorno_w	:= 'N';
	open C02;
	loop
	fetch C02 into	
		ds_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
	end loop;
	close C02;
	
end if;

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (ds_retorno_w	= 'S') then
	select	count(*)
	into STRICT	qt_itens_w
	from	atend_perda_ganho_controle
	where	nr_atendimento	= nr_atendimento_p
	and	IE_REGISTRA_PG	= 'S';
	
	if (qt_itens_w	> 0) then
		select	count(*)
		into STRICT	qt_itens_w
		from	atend_perda_ganho_controle
		where	nr_atendimento	= nr_atendimento_p
		and	nr_seq_tipo	= nr_seq_tipo_pg_p
		and	IE_REGISTRA_PG	= 'S';
		
		if (qt_itens_w	= 0) then
			ds_retorno_w	:= 'N';
		end if;
		
	end if;
	
	cd_setor_atendimento_w := Obter_Setor_Atendimento(nr_atendimento_p);
	
	select 	count(*)
	into STRICT	qt_itens_w
	from 	tipo_perda_ganho t,
		grupo_perda_ganho g,
		grupo_perda_ganho_lib l
	where 	t.nr_sequencia = nr_seq_tipo_pg_p
	and   	t.nr_seq_grupo = g.nr_sequencia
	and   	g.nr_sequencia = l.nr_seq_grupo;

	if (qt_itens_w	> 0) and (ds_retorno_w	= 'S')then
		begin
		
		open C01;
		loop
		fetch C01 into	
			ie_permite_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			ds_retorno_w :=  ie_permite_w;
			end;
		end loop;
		close C01;
		
		
		end;
	end if;
	
end if;
	
return ds_retorno_w;	
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
