CREATE OR REPLACE FUNCTION obter_se_tipo_guia_conta (ie_tipo_atendimento_p bigint, ie_tipo_guia_p text) RETURNS varchar AS $body$
DECLARE


ie_liberado_w	varchar(01)	:= 'S';
qt_reg_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from 	tipo_guia_conta;

if (qt_reg_w	> 0) then
	begin

	select	coalesce(max('S'), 'N')
	into STRICT	ie_liberado_w
	from	tipo_guia_conta
	where	ie_tipo_atendimento	= ie_tipo_atendimento_p
	and	ie_tipo_guia	= ie_tipo_guia_p;

	end;
end if;

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
