CREATE OR REPLACE FUNCTION obter_se_tipo_guia_lib (ie_tipo_atendimento_p bigint, cd_estabelecimento_p bigint, ie_tipo_guia_p text, ie_clinica_p bigint default 0) RETURNS varchar AS $body$
DECLARE


ie_liberado_w	varchar(01)	:= 'S';
qt_reg_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from 	TIPO_GUIA_ATEND
where	ie_tipo_atendimento = ie_tipo_atendimento_p;

if (qt_reg_w	> 0) then
	begin

	select	coalesce(max('S'), 'N')
	into STRICT	ie_liberado_w
	from	tipo_guia_atend
	where	ie_tipo_atendimento				= ie_tipo_atendimento_p
	and		ie_tipo_guia					= ie_tipo_guia_p
	and (coalesce(ie_clinica, 0)		= coalesce(ie_clinica_p, 0) or coalesce(ie_clinica::text, '') = '')
	and		coalesce(cd_estabelecimento,cd_estabelecimento_p)	= cd_estabelecimento_p;

	end;
end if;

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
