CREATE OR REPLACE FUNCTION obter_se_tipo_hist_usuario ( nr_seq_tipo_hist_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


qt_usuario_lib_w	bigint;
ie_liberado_w	varchar(1)	:= 'N';


BEGIN

select	count(*)
into STRICT	qt_usuario_lib_w
from	tipo_hist_pf_usuario a
where	a.nr_seq_tipo_hist	= nr_seq_tipo_hist_p;

ie_liberado_w	:= 'S';
if (qt_usuario_lib_w > 0) then
	select	count(*)
	into STRICT	qt_usuario_lib_w
	from	tipo_hist_pf_usuario b
	where	b.nr_seq_tipo_hist	= nr_seq_tipo_hist_p
	and	b.nm_usuario_lib	= nm_usuario_p;
	if (qt_usuario_lib_w = 0) then
		ie_liberado_w	:= 'N';
	end if;
end if;

return	ie_liberado_w;

end	;
