CREATE OR REPLACE FUNCTION obter_se_tipo_liberacao_quimio ( nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);


BEGIN

if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') THEN
	select	max(ie_tipo_liberacao)
	into STRICT	ie_retorno_w
	FROM    PACIENTE_SETOR_LIB a
	where   a.nr_seq_paciente = nr_seq_paciente_p
	and	ie_tipo_liberacao = 'F'
	and  	coalesce(dt_cancelamento::text, '') = '';
end if;

if (coalesce(ie_retorno_w::text, '') = '') and (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then
	select	max(ie_tipo_liberacao)
	into STRICT	ie_retorno_w
	FROM    PACIENTE_SETOR_LIB a
	where   a.nr_seq_paciente = nr_seq_paciente_p
	and	ie_tipo_liberacao = 'E'
	and  	coalesce(dt_cancelamento::text, '') = '';
end if;

if (coalesce(ie_retorno_w::text, '') = '') and (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then
	select	max(ie_tipo_liberacao)
	into STRICT	ie_retorno_w
	FROM    PACIENTE_SETOR_LIB a
	where   a.nr_seq_paciente = nr_seq_paciente_p
	and	ie_tipo_liberacao = 'N'
	and  	coalesce(dt_cancelamento::text, '') = '';
end if;

if (coalesce(ie_retorno_w::text, '') = '') and (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then
	select	max(ie_tipo_liberacao)
	into STRICT	ie_retorno_w
	FROM    PACIENTE_SETOR_LIB a
	where   a.nr_seq_paciente = nr_seq_paciente_p
	and	ie_tipo_liberacao = 'M'
	and  	coalesce(dt_cancelamento::text, '') = '';
end if;

return ie_retorno_w;

end;
