CREATE OR REPLACE FUNCTION obter_se_tipo_loc_setor ( cd_setor_registro_p bigint, ie_tipo_local_p text) RETURNS varchar AS $body$
DECLARE


qt_existe_regra_w	bigint;
ie_valido_w		varchar(1);


BEGIN

ie_valido_w	:= 'S';
if (cd_setor_registro_p IS NOT NULL AND cd_setor_registro_p::text <> '') and (ie_tipo_local_p IS NOT NULL AND ie_tipo_local_p::text <> '') then
	begin
	/* Verifica se o setor possui regra */

	select	count(*)
	into STRICT	qt_existe_regra_w
	from	regra_loc_laudo_setor
	where	cd_setor_atendimento	= cd_setor_registro_p;

	/* Se possuir regra para o setor verifica se o tipo de local Ã© valido para regra */

	if (qt_existe_regra_w > 0) then
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_valido_w
		from	regra_loc_laudo_setor
		where	cd_setor_atendimento	= cd_setor_registro_p
		and	ie_tipo_local		= ie_tipo_local_p;
	end if;
	end;
end if;

return ie_valido_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
