CREATE OR REPLACE FUNCTION obter_se_tipo_material ( cd_material_p bigint, ie_tipo_material_p text) RETURNS varchar AS $body$
DECLARE


ie_tipo_material_w		varchar(10);
ie_resultado_w                  varchar(1) := 'N';


BEGIN

select	max(ie_tipo_material)
into STRICT	ie_tipo_material_w
from	material
where	cd_material = cd_material_p;

if (ie_tipo_material_p = 'M') and (ie_tipo_material_w in ('1','5','7','10')) then
	ie_resultado_w	:= 'S';
elsif (ie_tipo_material_p = 'D') and (ie_tipo_material_w in ('0','2','3','4','8','9')) then
	ie_resultado_w	:= 'S';
elsif (ie_tipo_material_p = 'E') and (ie_tipo_material_w = '5') then
	ie_resultado_w	:= 'S';
elsif (ie_tipo_material_p = 'O') and (ie_tipo_material_w in ('1','5')) then
	ie_resultado_w	:= 'S';
end if;

return	ie_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
