CREATE OR REPLACE FUNCTION obter_se_tipo_plantao_regra (nr_seq_regra_p bigint, nr_seq_tipo_plantao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cont_w			bigint;
qt_regras_w		bigint;


BEGIN

ds_retorno_w	:= 'S';

select	count(a.nr_sequencia)
into STRICT	qt_regras_w
from	regra_esp_repasse a
where	a.nr_sequencia	= nr_seq_regra_p
and	coalesce(a.nr_seq_tipo_plantao::text, '') = ''
and (exists (	SELECT	1
		from	regra_esp_rep_tipo_plantao x
		where	x.nr_seq_regra_esp	= a.nr_sequencia
		and	coalesce(x.nr_seq_tipo_plantao::text, '') = '')
	or
	not exists (	select	1
			from	regra_esp_rep_tipo_plantao x
			where	x.nr_seq_regra_esp	= a.nr_sequencia));

 if (qt_regras_w = 0) then
	select	count(a.nr_sequencia)
	into STRICT	cont_w
	from	regra_esp_repasse a
	where	a.nr_sequencia		= nr_seq_regra_p
	and	a.nr_seq_tipo_plantao	= nr_seq_tipo_plantao_p;

	if (cont_w	= 0) then

		select	count(a.nr_sequencia)
		into STRICT	cont_w
		from	regra_esp_rep_tipo_plantao a
		where	a.nr_seq_regra_esp	= nr_seq_regra_p
		and	a.nr_seq_tipo_plantao	= nr_seq_tipo_plantao_p;

		if (cont_w = 0) then
			ds_retorno_w	:= 'N';
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
