CREATE OR REPLACE FUNCTION obter_se_tipo_prot_lib ( ie_tipo_protocolo_p text, cd_convenio_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1):='S';
ie_regra_w		integer;
ie_liberado_w		integer;


BEGIN

select 	count(*)
into STRICT	ie_regra_w
from	tipo_prot_lib_conv
where	cd_convenio =cd_convenio_p
and	cd_estabelecimento = cd_estabelecimento_p;

if (ie_regra_w > 0) then

	select 	count(*)
	into STRICT	ie_liberado_w
	from	tipo_prot_lib_conv
	where	cd_convenio = cd_convenio_p
	and	cd_estabelecimento = cd_estabelecimento_p
	and	ie_tipo_protocolo = ie_tipo_protocolo_p;

	if (ie_liberado_w > 0) then
		ie_retorno_w:='S';
	else
		ie_retorno_w:='N';
	end if;
else
	ie_retorno_w:='S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
