CREATE OR REPLACE FUNCTION obter_se_tipo_receb_lib (cd_tipo_receb_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(255);
cont_w		bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	tipo_receb_lib
where	cd_tipo_recebimento	= cd_tipo_receb_p;

ie_retorno_w	:= 'S';
if (cont_w > 0) then
	ie_retorno_w	:= 'N';
	select	count(*)
	into STRICT	cont_w
	from	tipo_receb_lib
	where	cd_tipo_recebimento	= cd_tipo_receb_p
	and	cd_perfil		= cd_perfil_p;
	if (cont_w > 0) then
		ie_retorno_w	:= 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
