CREATE OR REPLACE FUNCTION obter_se_titulos_vencidos (cd_pessoa_fisica_p text, cd_cgc_p text, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(20);
qt_titulos_w	bigint;
qt_titulo_pf_w	bigint;
qt_titulo_pj_w	bigint;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	count(*)
	into STRICT	qt_titulo_pf_w
	from	titulo_receber
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	dt_vencimento	< dt_referencia_p
	and	vl_saldo_titulo > 0;
end if;

if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	select	count(*)
	into STRICT	qt_titulo_pf_w
	from	titulo_receber
	where	cd_cgc = cd_cgc_p
	and	dt_vencimento	< dt_referencia_p
	and	vl_saldo_titulo > 0;
end if;

qt_titulos_w := qt_titulo_pf_w + qt_titulo_pj_w;

if (qt_titulos_w = 0) then
	ds_retorno_w := 'N';
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
