CREATE OR REPLACE FUNCTION obter_se_titulo_conta_liquid (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ie_situacao_w	varchar(1)	:= 'N';


BEGIN
if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') then
	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_situacao_w
	from	titulo_receber
	where	(dt_liquidacao IS NOT NULL AND dt_liquidacao::text <> '')
	and	nr_interno_conta	= nr_interno_conta_p;
end if;

RETURN	ie_situacao_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
