CREATE OR REPLACE FUNCTION obter_se_titulo_pago (nr_seq_mensalidade_p bigint, dt_liquidacao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
qt_registro_W	bigint;


BEGIN

select 	count(*)
into STRICT	qt_registro_W
from	titulo_receber r,
	pls_mensalidade p
where	r.nr_seq_mensalidade = p.nr_sequencia
and	p.nr_sequencia = nr_seq_mensalidade_p
and	to_char(r.dt_liquidacao, 'YYYY') = dt_liquidacao_p
and	((r.vl_saldo_titulo < vl_titulo)
or (r.ie_situacao = 2));

if qt_registro_W > 0 then
	ds_retorno_w	:= 'S';
else
	ds_retorno_w	:= 'N';
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
