CREATE OR REPLACE FUNCTION obter_se_titulo_pls (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_pls_w	varchar(1)	:= 'N';


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	select	coalesce(max(ie_pls),'N')
	into STRICT	ie_pls_w
	from	titulo_receber
	where	nr_titulo	= nr_titulo_p;
end if;

return ie_pls_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
