CREATE OR REPLACE FUNCTION obter_se_tit_baixado_parcial (nr_titulo_receber_p bigint, nr_titulo_pagar_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w	varchar(1)	:= 'N';
vl_saldo_tit_w	double precision	:= 0;
vl_titulo_w	double precision	:= 0;
vl_alteracao_w	double precision	:= 0;
ie_situacao_w	varchar(1);


BEGIN 
 
if (coalesce(nr_titulo_receber_p,0) > 0) then 
 
	select	ie_situacao 
	into STRICT	ie_situacao_w 
	from	titulo_receber 
	where	nr_titulo	= nr_titulo_receber_p;
 
	if (ie_situacao_w = '1') then 
	 
		select	coalesce(obter_saldo_titulo_receber(nr_titulo, trunc(dt_referencia_p)),0), 
			coalesce(vl_titulo,0) 
		into STRICT	vl_saldo_tit_w, 
			vl_titulo_w 
		from	titulo_receber 
		where	nr_titulo	= nr_titulo_receber_p;
	 
		SELECT	coalesce(sum(CASE WHEN ie_aumenta_diminui='A' THEN  vl_alteracao  ELSE vl_alteracao * -1 END ),0) 
		INTO STRICT	vl_alteracao_w 
		FROM	alteracao_valor 
		WHERE	nr_titulo	= nr_titulo_receber_p;
	 
		vl_titulo_w		:= coalesce(vl_titulo_w,0) + coalesce(vl_alteracao_w,0);
	 
		if (vl_saldo_tit_w	= 0) or (vl_titulo_w	= vl_saldo_tit_w) then 
			ie_retorno_w := 'N';
		else 
			ie_retorno_w := 'S';
		end if;
	end if;
end if;
 
RETURN	ie_retorno_w;
 
END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
