CREATE OR REPLACE FUNCTION obter_se_tit_nf_venc_pagar (nr_seq_nf_p bigint, dt_vencimento_p timestamp) RETURNS varchar AS $body$
DECLARE


nr_titulo_w	bigint;
ie_retorno_w	varchar(1);


BEGIN

select	coalesce(max(nr_titulo),0)
into STRICT	nr_titulo_w
from	titulo_pagar
where	nr_seq_nota_fiscal			= nr_seq_nf_p
and	trunc(dt_vencimento_original, 'dd')	= trunc(dt_vencimento_p, 'dd');

if (nr_titulo_w > 0) then
	ie_retorno_w	:= 'S';
else
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
