CREATE OR REPLACE FUNCTION obter_se_tit_nivel_lib (nr_titulo_p bigint, ie_nivel_p bigint) RETURNS varchar AS $body$
DECLARE


qt_nivel_inferior_w		bigint;
qt_nivel_superior_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_nivel_inferior_w
from	conta_pagar_lib a
where	a.nr_titulo	= nr_titulo_p
and	coalesce(a.ie_nivel, 0)	< coalesce(ie_nivel_p, 0)
and	((coalesce(a.dt_liberacao::text, '') = '') and (coalesce(a.dt_reprovacao::text, '') = ''));

select	count(*)
into STRICT	qt_nivel_superior_w
from	conta_pagar_lib a
where	a.nr_titulo	= nr_titulo_p
and	coalesce(a.ie_nivel, 0)	> coalesce(ie_nivel_p, 0)
and	((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.dt_reprovacao IS NOT NULL AND a.dt_reprovacao::text <> ''));

if	(qt_nivel_inferior_w = 0 AND qt_nivel_superior_w = 0) then
	return	'S';
else
	return	'N';
end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_tit_nivel_lib (nr_titulo_p bigint, ie_nivel_p bigint) FROM PUBLIC;

