CREATE OR REPLACE FUNCTION obter_se_tit_pagar_desdob (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(100);
cont_w		bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	titulo_pagar_desdob
where	nr_titulo_dest	= nr_titulo_p;

ds_retorno_w			:= 'N';
if (cont_w > 0) then
	ds_retorno_w		:= 'S';
end if;

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
