CREATE OR REPLACE FUNCTION obter_se_tit_pagar_transf (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'N';
nr_titulo_transf_w	bigint;
ie_situacao_w		varchar(2);
nr_titulo_w		bigint;


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	select	max(nr_titulo_transf)
	into STRICT	nr_titulo_w
	from	titulo_pagar
	where	nr_titulo	= nr_titulo_p;

	if (nr_titulo_transf_w IS NOT NULL AND nr_titulo_transf_w::text <> '') then
		select	max(ie_situacao)
		into STRICT	ie_situacao_w
		from	titulo_pagar
		where	nr_titulo	= nr_titulo_transf_w;

		if (ie_situacao_w <> 'C') then
			ie_retorno_w	:= 'S';
		end if;
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
