CREATE OR REPLACE FUNCTION obter_se_tit_pagar_unificado (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
qt_registros_w	bigint;
ie_origem_titulo_w titulo_pagar.ie_origem_titulo%type;


BEGIN
ds_retorno_w := 'N';

select coalesce(max(ie_origem_titulo),21)
into STRICT  ie_origem_titulo_w
from titulo_pagar
where nr_titulo = nr_titulo_p;

select count(*)
into STRICT qt_registros_w
from titulo_pagar
where nr_titulo_dest = nr_titulo_p;

if (ie_origem_titulo_w = 21) and (qt_registros_w > 0) then --unificação
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
