CREATE OR REPLACE FUNCTION obter_se_tit_possui_anexo ( ie_tipo_titulo_p text, nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE



/*      							A T E N Ç Ã O
    Essa funtion foi criada para ser utilizada na função Títulos a Pagar, para o relatório gerado na função, pela aba Relat,
    quando selecionado a opção de coluna adicional Possui anexo. Muito cuidado ao alterar essa rotina pois pode impactar  no relat gerado.

ie_tipo_titulo_p
'R' = Títulos a Receber
'P' = Títulos a Pagar
*/
ds_retorno_w	varchar(3);


BEGIN

if (coalesce(nr_titulo_p,0) <> 0) then

	if (coalesce(ie_tipo_titulo_p,'X') = 'P') then

		select	CASE WHEN count(a.nr_sequencia)=0 THEN wheb_mensagem_pck.get_texto(786729)  ELSE wheb_mensagem_pck.get_texto(786728) END
		into STRICT	ds_retorno_w
		from	titulo_pagar_arq a
		where	a.nr_titulo = nr_titulo_p;

	elsif (coalesce(ie_tipo_titulo_p,'X') = 'R') then

		select	CASE WHEN count(a.nr_sequencia)=0 THEN wheb_mensagem_pck.get_texto(786729)  ELSE wheb_mensagem_pck.get_texto(786728) END
		into STRICT	ds_retorno_w
		from	titulo_receber_arq a
		where	a.nr_titulo = nr_titulo_p;

	end if;

end if;

return	substr(coalesce(ds_retorno_w,wheb_mensagem_pck.get_texto(786729)),1,10);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
