SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_todos_itens_oc_reprov (nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE


ie_todos_reprovados_w		varchar(1) := 'N';
qt_itens_w			bigint;
qt_itens_reprov_w			bigint;



BEGIN

select	count(*)
into STRICT	qt_itens_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p;

select	count(*)
into STRICT	qt_itens_reprov_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
and	(dt_reprovacao IS NOT NULL AND dt_reprovacao::text <> '');

if (qt_itens_w > 0) and (qt_itens_w = qt_itens_reprov_w) then
	ie_todos_reprovados_w := 'S';
end if;

return	ie_todos_reprovados_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
