CREATE OR REPLACE FUNCTION obter_se_todos_paracer_lib ( nr_seq_lista_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_item_w		bigint;
qt_existe_w		bigint;
ie_retorno_w		varchar(1);

c01 CURSOR FOR
SELECT	nr_sequencia
from	segmento_compras_rel_it
where	nr_seq_seg_rel = nr_seq_lista_p;


BEGIN

ie_retorno_w	:= 'S';

open c01;
loop
fetch c01 into
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (ie_retorno_w = 'S') then
		begin

		select	count(*)
		into STRICT	qt_existe_w
		from	segmento_compras_rel_parec
		where	nr_seq_item_rel = nr_seq_item_w
		and	coalesce(dt_liberacao_parec::text, '') = '';

		if (qt_existe_w > 0) then
			ie_retorno_w	:= 'N';
		end if;

		end;
	end if;

	end;
end loop;
close c01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
