CREATE OR REPLACE FUNCTION obter_se_todos_pj_desab ( nr_seq_licitacao_p bigint, nr_Seq_lic_item_p bigint, nr_seq_lance_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w		bigint;
ie_existe_w		varchar(1) := 'N';


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	reg_lic_fornec_lance
where	nr_seq_licitacao	= nr_seq_licitacao_p
and	nr_seq_lic_item	= nr_seq_lic_item_p
and	nr_seq_lance	= nr_seq_lance_p;

if (qt_existe_w > 0) then
	select	count(*)
	into STRICT	qt_existe_w
	from	reg_lic_fornec_lance a
	where	a.nr_seq_licitacao	= nr_seq_licitacao_p
	and	a.nr_seq_lic_item	= nr_seq_lic_item_p
	and	a.nr_seq_lance	= nr_seq_lance_p
	and	a.ie_qualificado	= 'S'
	and exists (
		SELECT	1
		from	reg_lic_fornec x
		where	a.nr_seq_fornec = x.nr_Sequencia
		and	ie_situacao = 'A');


	if (qt_existe_w = 0) then
		ie_existe_w := 'S';
	end if;
end if;

return	ie_existe_w;

end;
