CREATE OR REPLACE FUNCTION obter_se_tolera_variacao_disp (cd_material_p bigint, qt_dispensado_p bigint, qt_consumido_p bigint) RETURNS varchar AS $body$
DECLARE


ie_possui_regra_w	varchar(1) := 'N';
ie_tolera_w		varchar(1) := 'N';
cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;
qt_porc_tolerada_w	double precision :=0;
qt_porc_tolerada_ww	double precision :=0;
qt_porc_consumida_w	double precision :=0;
qt_porc_descartada_w	double precision :=0;

c01 CURSOR FOR
	SELECT	max(qt_porcentagem)
	from	regra_tolerancia_disp
	where	coalesce(cd_material,coalesce(cd_material_p,0))			= coalesce(cd_material_p,0)
	and	coalesce(cd_grupo_material,coalesce(cd_grupo_material_w,0))	= coalesce(cd_grupo_material_w,0)
	and	coalesce(cd_subgrupo_material,coalesce(cd_subgrupo_material_w,0))	= coalesce(cd_subgrupo_material_w,0)
	and	coalesce(cd_classe_material,coalesce(cd_classe_material_w,0))	= coalesce(cd_classe_material_w,0)
	and	coalesce(ie_situacao,'A') 					= 'A'
	order by	coalesce(cd_grupo_material,0),
			coalesce(cd_subgrupo_material,0),
			coalesce(cd_classe_material,0),
			coalesce(cd_material,0);



BEGIN
if (coalesce(qt_dispensado_p,0) > 0) then
	select	coalesce(max('S'),'N')
	into STRICT	ie_possui_regra_w
	from	regra_tolerancia_disp
	where	coalesce(ie_situacao,'A') = 'A';

	if (ie_possui_regra_w = 'S') then
		select	max(cd_grupo_material),
			max(cd_subgrupo_material),
			max(cd_classe_material)
		into STRICT	cd_grupo_material_w,
			cd_subgrupo_material_w,
			cd_classe_material_w
		from	estrutura_material_v
		where	cd_material = cd_material_p;

		open c01;
		loop
		fetch c01 into
			qt_porc_tolerada_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			qt_porc_tolerada_ww := qt_porc_tolerada_w;
			end;
		end loop;
		close C01;

		if (qt_porc_tolerada_ww > 0) then
			qt_porc_consumida_w	:= dividir((coalesce(qt_consumido_p,0) * 100),coalesce(qt_dispensado_p,0));
			qt_porc_descartada_w	:= 100 - qt_porc_consumida_w;

			if (qt_porc_descartada_w > qt_porc_tolerada_ww) then
				ie_tolera_w := 'N';
			else
				ie_tolera_w := 'S';
			end if;
		end if;
	end if;
end if;

return ie_tolera_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
