CREATE OR REPLACE FUNCTION obter_se_topografia_associada ( nr_seq_topografia_p bigint, nr_seq_result_p bigint default null, nr_seq_item_p bigint default null) RETURNS varchar AS $body$
DECLARE


ie_associado_w		varchar(1) := 'S';
qt_reg_resultado_w	bigint;
qt_reg_item_w		bigint;
qt_reg_w		bigint;


BEGIN


if (nr_seq_topografia_p IS NOT NULL AND nr_seq_topografia_p::text <> '') and (nr_seq_result_p IS NOT NULL AND nr_seq_result_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then

	select	count(*)
	into STRICT	qt_reg_resultado_w
	from	pe_item_topografia
	where	nr_seq_result = nr_seq_result_p;

	if (qt_reg_resultado_w > 0) then

		select	count(*)
		into STRICT	qt_reg_w
		from	pe_item_topografia
		where	nr_seq_result = nr_seq_result_p
		and	nr_seq_topografia = nr_seq_topografia_p;

		if (qt_reg_w = 0) then
			ie_associado_w	:= 'N';
		end if;

	else

		select	count(*)
		into STRICT	qt_reg_item_w
		from	pe_item_topografia
		where	nr_seq_item = nr_seq_item_p;

		if (qt_reg_item_w > 0) then

		select	count(*)
		into STRICT	qt_reg_w
		from	pe_item_topografia
		where	nr_seq_item = nr_seq_item_p
		and	nr_seq_topografia = nr_seq_topografia_p;

		if (qt_reg_w = 0) then
			ie_associado_w	:= 'N';
		end if;

		end if;

	end if;
end if;

return	ie_associado_w;

end;
