CREATE OR REPLACE FUNCTION obter_se_trans_financ_ativa (nr_seq_trans_financ_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	bigint;
count_w		bigint;

BEGIN

if (nr_seq_trans_financ_p IS NOT NULL AND nr_seq_trans_financ_p::text <> '') then

	select	coalesce(count(*),0)
	into STRICT	count_w
	from	transacao_financeira
	where	nr_sequencia	= nr_seq_trans_financ_p
	and 	ie_situacao	= 'A';

	if (count_w > 0) then
		ds_retorno_w := nr_seq_trans_financ_p;
	else
		ds_retorno_w := null;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
