CREATE OR REPLACE FUNCTION obter_se_trans_fin_caixa (nr_seq_caixa_p bigint, nr_seq_trans_financ_p bigint) RETURNS varchar AS $body$
DECLARE


cont_w			bigint;
nr_seq_classif_w	bigint;
ds_retorno_w		varchar(2);


BEGIN

select	count(*)
into STRICT	cont_w
from	trans_financ_caixa
where	nr_seq_transacao	= nr_seq_trans_financ_p;

ds_retorno_w	:= 'S';

if (cont_w > 0) then

	select	max(nr_seq_classif)
	into STRICT	nr_seq_classif_w
	from	caixa
	where	nr_sequencia		= nr_seq_caixa_p;

	select	count(*)
	into STRICT	cont_w
	from	trans_financ_caixa
	where	nr_seq_classif_caixa	= nr_seq_classif_w
	and	nr_seq_transacao	= nr_seq_trans_financ_p;

	if (cont_w = 0) then
		ds_retorno_w	:= 'N';
	end if;

	select	count(*)
	into STRICT	cont_w
	from	trans_financ_caixa
	where	nr_seq_caixa		= nr_seq_caixa_p
	and	nr_seq_transacao	= nr_seq_trans_financ_p;

	if (cont_w > 0) then
		ds_retorno_w	:= 'S';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
