CREATE OR REPLACE FUNCTION obter_se_trans_fin_estab (nr_seq_trans_financ_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';
qt_registro_w		bigint;
cd_estabelecimento_w	smallint;


BEGIN

if (nr_seq_trans_financ_p IS NOT NULL AND nr_seq_trans_financ_p::text <> '') then
	select	cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	transacao_financeira
	where	nr_sequencia	= nr_seq_trans_financ_p;

	if (coalesce(cd_estabelecimento_w::text, '') = '') then
		select	count(*)
		into STRICT	qt_registro_w
		from	trans_financ_estab
		where	nr_seq_trans_financ	= nr_seq_trans_financ_p;

		if (qt_registro_w > 0) then
			select	count(*)
			into STRICT	qt_registro_w
			from	trans_financ_estab
			where	nr_seq_trans_financ	= nr_seq_trans_financ_p
			and	cd_estab_liberado	= cd_estabelecimento_p;

			if (qt_registro_w > 0) then
				ie_retorno_w	:= 'S';
			else
				ie_retorno_w	:= 'N';
			end if;
		else
			ie_retorno_w	:= 'S';
		end if;

	elsif (cd_estabelecimento_p = cd_estabelecimento_w) then
		ie_retorno_w	:= 'S';
	else
		ie_retorno_w	:= 'N';
	end if;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
