CREATE OR REPLACE FUNCTION obter_se_trans_fin_usuario (nm_usuario_p text, nr_seq_trans_financ_p bigint, cd_perfil_p bigint default null) RETURNS varchar AS $body$
DECLARE


cont_w		integer;
ds_retorno_w	varchar(2):= 'N';
cd_perfil_w	bigint;
ie_exige_perfil_lib_w	varchar(1) := 'N';


BEGIN

if (coalesce(cd_perfil_p,0) > 0) then
	cd_perfil_w	:= cd_perfil_p;
else
	cd_perfil_w	:= obter_perfil_ativo;
end if;

if (obter_funcao_ativa = 851) then
	begin
	ie_exige_perfil_lib_w := obter_param_usuario(851, 176, cd_perfil_w, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_exige_perfil_lib_w);

	if (ie_exige_perfil_lib_w = 'N') then
		ds_retorno_w:=	'S';
	end if;
	end;
end if;

ds_retorno_w:=	'S';

select	count(*)
into STRICT	cont_w
from	trans_financ_usuario
where	nr_seq_transacao	= nr_seq_trans_financ_p;

if (cont_w > 0) then /*Se existir algum usuario ou perfil liberado para essa transcao, entao essa transacao so deve ser liberada para esse usuario ou perfil*/
	begin
	ds_retorno_w:=	'N';

	-- Caso consista o parâmetro e o usuário não possuir nenhnuma liberação, então efetuar a liberação de todos os perfis.
	/*select	count(*)
	into	cont_w
	from	trans_financ_usuario
	where	((cd_perfil		= cd_perfil_w) or (nm_usuario_param	= nm_usuario_p));

	if (cont_w = 0) then
		ds_retorno_w:=	'S';
	end if;		*/
 /*aamfirmo retirei na OS 792347 conforme verificado com alessandro.Não faz sentido liberar a TF se nao existir registro para esse perfil ou usuario em qq transacao.*/
	select	count(*)
	into STRICT	cont_w
	from	trans_financ_usuario
	where	((cd_perfil		= cd_perfil_w) or (nm_usuario_param	= nm_usuario_p))
	and	nr_seq_transacao	= nr_seq_trans_financ_p;

	if (cont_w > 0) then /*se o sistema encontrar o usuario ou perfil no cadastro, vai liberar.*/
		ds_retorno_w:=	'S';
	end if;
	end;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
