SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_trans_pagar (nr_seq_trans_financ_p bigint, ie_tipo_titulo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
qt_registros_w	bigint;
qt_liberados_w	bigint;


BEGIN

ds_retorno_w := 'N';

select	count(*)
into STRICT	qt_registros_w
from	trans_financ_bordero a
where	a.nr_seq_trans_financ	= nr_seq_trans_financ_p;


if (qt_registros_w > 0) then
	begin
	select	count(*)
	into STRICT	qt_liberados_w
	from	transacao_financeira a,
		trans_financ_bordero b
	where	a.nr_sequencia = nr_seq_trans_financ_p
	and	a.nr_sequencia = b.nr_seq_trans_financ
	and	b.ie_tipo_titulo = ie_tipo_titulo_p;
	end;
end if;

if (qt_registros_w = 0) or (qt_liberados_w > 0) then
	ds_retorno_w		:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
