CREATE OR REPLACE FUNCTION obter_se_tratamento_autorizado (nr_seq_rxt_tratamento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_autorizado_w	varchar(2);


BEGIN

	SELECT  CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END
	INTO STRICT 	ie_autorizado_w
	FROM    autorizacao_convenio a,
		estagio_autorizacao b
	WHERE   a.nr_seq_estagio	= b.nr_sequencia
	AND 	b.ie_interno		= 10
	AND 	a.ie_tipo_autorizacao 	= 3
	AND 	a.nr_seq_rxt_tratamento	= nr_seq_rxt_tratamento_p;

	return ie_autorizado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
