SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_triagem_senha (IE_OPCAO_P text) RETURNS varchar AS $body$
DECLARE


QTD_REGRAS_W bigint;
IE_TRIAGEM_W varchar(5);


BEGIN

  IF (IE_OPCAO_P = 'E') THEN



    SELECT COUNT(*)

    INTO STRICT QTD_REGRAS_W

    FROM REGRA_SENHA_TRIAGEM R

    WHERE R.CD_ESTABELECIMENTO = WHEB_USUARIO_PCK.get_cd_estabelecimento


    OR R.CD_PERFIL = WHEB_USUARIO_PCK.get_cd_perfil

    AND R.IE_SITUACAO = 'A';



    IF (coalesce(QTD_REGRAS_W, 0) > 0) THEN

      RETURN 'S';
    ELSE
      RETURN 'N';

    END IF;


  ELSIF (IE_OPCAO_P = 'T') THEN
  
    SELECT MAX(R.IE_TRIAGEM)

    INTO STRICT IE_TRIAGEM_W

    FROM REGRA_SENHA_TRIAGEM R

    WHERE R.CD_ESTABELECIMENTO = WHEB_USUARIO_PCK.get_cd_estabelecimento

    AND R.CD_PERFIL = WHEB_USUARIO_PCK.get_cd_perfil

    AND R.IE_SITUACAO = 'A';

    
    RETURN IE_TRIAGEM_W;

  END IF;



  RETURN 'N';


END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_triagem_senha (IE_OPCAO_P text) FROM PUBLIC;

