CREATE OR REPLACE FUNCTION obter_se_tributo_nf ( nr_seq_nf_p bigint, nr_item_nf_p bigint, cd_tributo_p bigint, nr_seq_interno_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
NF - NOTA_FISCAL_TRIB
NI - NOTA_FISCAL_ITEM_TRIB
NV - NOTA_FISCAL_VENC_TRIB
*/
qt_registros		integer := 0;


BEGIN

select	count(*)
into STRICT	qt_registros
from	nota_fiscal_trib
where	nr_sequencia = nr_seq_nf_p
and	cd_tributo = cd_tributo_p
and	nr_seq_interno <> nr_seq_interno_p;

if (qt_registros > 0) then
	return	'S';
else
	return	'N';
end if;

end;
