CREATE OR REPLACE FUNCTION obter_se_tributo_retido ( cd_tributo_p bigint, cd_pessoa_fisica_p text, ie_situacao_tit_p text, ie_situacao_rep_p text, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_cont_w		bigint;
ie_resultado_w		varchar(1);


BEGIN

ie_resultado_w	:= 'N';

select	count(*)
into STRICT	qt_cont_w
from	pessoa_fisica_trib a
where	a.cd_pessoa_fisica		= cd_pessoa_fisica_p
and	a.cd_tributo		= cd_tributo_p
and	a.ie_pago_prev_tit_pagar	= coalesce(ie_situacao_tit_p,a.ie_pago_prev_tit_pagar)
and	a.ie_pago_prev_rep	= coalesce(ie_situacao_rep_p,a.ie_pago_prev_rep)
and	dt_referencia_p		between a.dt_inicio_vigencia and fim_dia(a.dt_fim_vigencia);

if (qt_cont_w > 0) then
	ie_resultado_w	:= 'S';
end if;

return ie_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
