CREATE OR REPLACE FUNCTION obter_se_trib_item (nr_seq_nota_p bigint, nr_item_nf_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(1);
qtd_w integer;


BEGIN

select   count(*)
into STRICT       qtd_w
from      nota_fiscal_item_trib
where   nr_sequencia = nr_seq_nota_p
and       nr_item_nf =  nr_item_nf_p;


if (qtd_w > 0) then

ds_retorno_w := 'S';

else

ds_retorno_w := 'N';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
