CREATE OR REPLACE FUNCTION obter_se_turno_esp_agecons_vig (dt_inicial_p timestamp, dt_final_p timestamp, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


dt_inicial_w	timestamp;
dt_final_w	timestamp;
dt_agenda_w	timestamp;
ie_vigente_w	varchar(1) := 'N';


BEGIN

dt_inicial_w	:= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_inicial_p);
dt_final_w	:= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_final_p);
dt_agenda_w	:= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_agenda_p);

if (dt_inicial_w IS NOT NULL AND dt_inicial_w::text <> '') and (dt_agenda_w IS NOT NULL AND dt_agenda_w::text <> '') then

	if (dt_final_w IS NOT NULL AND dt_final_w::text <> '') then

		if (dt_inicial_w <= dt_agenda_w) and (dt_final_w >= dt_agenda_w) then
			ie_vigente_w := 'S';
		else
			ie_vigente_w := 'N';
		end if;


	elsif (dt_inicial_w = dt_agenda_w) then
		ie_vigente_w := 'S';

	else
		ie_vigente_w := 'N';
	end if;
end if;

return ie_vigente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
