CREATE OR REPLACE FUNCTION obter_se_ultima_consulta_oft (nr_seq_consulta_p bigint, nr_atendimento_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(1) := 'N';


BEGIN

if (nr_seq_consulta_p IS NOT NULL AND nr_seq_consulta_p::text <> '') and
   (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '' AND nr_atendimento_p <> 0) then

	select coalesce(max('S'),'N')
	into STRICT   ds_retorno_w
	from   oft_consulta
	where  nr_sequencia = nr_seq_consulta_p
	and    dt_consulta = (SELECT max(dt_consulta)
	   		      from	  oft_consulta
			      where    nr_atendimento = nr_atendimento_p
					and		coalesce(dt_cancelamento::text, '') = '');

elsif (nr_seq_consulta_p IS NOT NULL AND nr_seq_consulta_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select coalesce(max('S'),'N')
	into STRICT   ds_retorno_w
	from   oft_consulta
	where  nr_sequencia = nr_seq_consulta_p
	and    dt_consulta = (SELECT max(dt_consulta)
	   		      from	  oft_consulta
			      where    cd_pessoa_fisica = cd_pessoa_fisica_p
					and		coalesce(dt_cancelamento::text, '') = '');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
