CREATE OR REPLACE FUNCTION obter_se_ultima_ordem_pac (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_ultimo_w	varchar(1);

BEGIN

SELECT coalesce(MAX('N'),'S')
into STRICT	ie_ultimo_w
from   can_ordem_prod a
where  trunc(a.dt_prevista) = trunc(clock_timestamp())
and    a.cd_pessoa_fisica = cd_pessoa_fisica_p
AND    coalesce(a.ie_conferido,'N') = 'N';

return	ie_ultimo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
