CREATE OR REPLACE FUNCTION obter_se_ultimo_deposito (nr_seq_deposito_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);


BEGIN

  select	'S'
  into STRICT	ie_retorno_w
  from	banco_aplic_deposito a
