CREATE OR REPLACE FUNCTION obter_se_ultimo_rendimento (nr_seq_rendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'N';
qt_rend_w	bigint;


BEGIN
select	count(*)
into STRICT	qt_rend_w
from	banco_rendimento a
where	a.nr_sequencia = nr_seq_rendimento_p
and	a.dt_rendimento = (SELECT max(x.dt_rendimento)
			from banco_rendimento x
			where x.nr_seq_aplicacao = a.nr_seq_aplicacao);

if (coalesce(qt_rend_w,0) > 0) then
	ie_retorno_w := 'S';
else
	ie_retorno_w := 'N';
end if;

return ie_retorno_w;

end;
