SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_ultimo_status ( ie_tipo_agenda_p bigint, nr_seq_agenda_p bigint, ie_evento_p text, ie_agenda_p text ) RETURNS varchar AS $body$
DECLARE


ie_last_status_w	varchar(1) := 'N';


BEGIN

if 	(
	(coalesce(ie_tipo_agenda_p,0) > 0) 	and (coalesce(nr_seq_agenda_p,0) > 0) 	and (coalesce(ie_evento_p,'N') <> 'N') 	and (coalesce(ie_agenda_p,'N') <> 'N')
	) then

	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT 	ie_last_status_w
	from	status_evento_agenda a
	where (a.cd_estabelecimento = obter_estabelecimento_ativo or exists (	SELECT 1 
										from STATUS_EVENTO_AGENDA_ESTAB x 
										where x.cd_estabelecimento = obter_estabelecimento_ativo
										and a.nr_sequencia 	   = x.nr_seq_status_evento_ag ))	
	and	a.ie_evento = ie_evento_p 	
	and	((a.ie_agenda = ie_agenda_p) or (a.ie_agenda = 'T'))
	and 	coalesce(a.ie_status,'0') = substr(obter_dados_agendas(ie_tipo_agenda_p,nr_seq_agenda_p,'SA'),1,255)  
	order 	by	coalesce(a.cd_agenda,0),
			coalesce(a.cd_perfil,0),
			coalesce(a.ie_evolucao_clinica,'0'),
			coalesce(a.ie_classif_agenda,'*'),
			coalesce(a.cd_convenio,0),
			coalesce(a.nr_seq_tipo_atend,0),
			a.ie_status LIMIT 1;

end if;

return ie_last_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
